mapper.select:
SELECT * FROM(SELECT C.CT_NO, C.CT_NM, G.GRADE_NM, E.EMP_NM, C.CT_PH, NVL(P.POINT, 0) AS POINT, C.CT_SEX, TO_CHAR(C.CT_REG_DATE,'YYYY-MM-DD') AS RDT, TO_CHAR(C.CT_VISIT_DATE,'YYYY-MM-DD') AS VDT, ROW_NUMBER() OVER(ORDER BY C.CT_NO DESC) AS RNUM FROM CUSTOMER C INNER JOIN EMPLOYEE E ON C.EMP_NO = E.EMP_NO INNER JOIN CUSTOMER_GRADE G ON C.CT_GRADE_NO = G.GRADE_NO LEFT JOIN (SELECT O.CT_NO, O.POINT FROM(SELECT CT_NO, SUM(PT_SAVE)-SUM(PT_DD) AS POINT FROM POINT GROUP BY CT_NO) O ) P ON C.CT_NO = P.CT_NO
AND TO_CHAR(C.CT_REG_DATE,'YYYY-MM-DD') BETWEEN #{startDate} AND #{endDate}
WHERE C.DEL_YN = 1
AND TO_CHAR(C.CT_REG_DATE,'YYYY-MM-DD') BETWEEN #{startDate} AND #{endDate}
AND C.CT_NM LIKE '%' || #{searchTxt} || '%' OR G.GRADE_NM LIKE '%' || #{searchTxt} || '%' OR E.EMP_NM LIKE'%' || #{searchTxt} || '%' OR C.CT_PH LIKE'%' || #{searchTxt} || '%'
AND C.CT_NM LIKE '%' || #{searchTxt} || '%'
AND G.GRADE_NM LIKE '%' || #{searchTxt} || '%'
AND E.EMP_NM LIKE'%' || #{searchTxt} || '%'
AND C.CT_PH LIKE'%' || #{searchTxt} || '%'
) SQ WHERE SQ.RNUM BETWEEN #{startCnt} AND #{endCnt}

SELECT COUNT(*) FROM(SELECT C.CT_NO, C.CT_NM, G.GRADE_NM, E.EMP_NM, C.CT_PH, NVL(P.POINT, 0) AS POINT, C.CT_SEX, TO_CHAR(C.CT_REG_DATE,'YYYY-MM-DD') AS RDT, TO_CHAR(C.CT_VISIT_DATE,'YYYY-MM-DD') AS VDT, ROW_NUMBER() OVER(ORDER BY C.CT_NO DESC) AS RNUM FROM CUSTOMER C INNER JOIN EMPLOYEE E ON C.EMP_NO = E.EMP_NO INNER JOIN CUSTOMER_GRADE G ON C.CT_GRADE_NO = G.GRADE_NO LEFT JOIN (SELECT O.CT_NO, O.POINT FROM(SELECT CT_NO, SUM(PT_SAVE)-SUM(PT_DD) AS POINT FROM POINT GROUP BY CT_NO) O WHERE CT_NO = 1) P ON C.CT_NO = P.CT_NO AND C.CT_NO = 1
AND TO_CHAR(C.CT_REG_DATE,'YYYY-MM-DD') BETWEEN #{startDate} AND #{endDate}
WHERE C.DEL_YN = 1
AND TO_CHAR(C.CT_REG_DATE,'YYYY-MM-DD') BETWEEN #{startDate} AND #{endDate}
AND C.CT_NM LIKE '%' || #{searchTxt} || '%' OR G.GRADE_NM LIKE '%' || #{searchTxt} || '%' OR E.EMP_NM LIKE'%' || #{searchTxt} || '%' OR C.CT_PH LIKE'%' || #{searchTxt} || '%'
AND C.CT_NM LIKE '%' || #{searchTxt} || '%'
AND G.GRADE_NM LIKE '%' || #{searchTxt} || '%'
AND E.EMP_NM LIKE'%' || #{searchTxt} || '%'
AND C.CT_PH LIKE'%' || #{searchTxt} || '%'
) SQ

mapper.update:
UPDATE CUSTOMER SET DEL_YN = 0
WHERE DEL_YN = 1
AND CT_NO = #{ct_no}
