mapper.select:
SELECT RV_NO, RDATE, RTIME, CT_NM, RV_PH, EMP_NM,RV_CON FROM(SELECT RE.RV_NO , TO_CHAR(RE.RV_DATE,'YYYY-MM-DD') AS RDATE , TO_CHAR(RE.RV_DATE,'HH24:MI') AS RTIME, C.CT_NM, RE.RV_PH , E.EMP_NM,RE.RV_CON, ROW_NUMBER() OVER(ORDER BY RE.RV_NO) AS RNUM FROM RESERVATION RE INNER JOIN CUSTOMER C ON RE.CT_NO = C.CT_NO INNER JOIN EMPLOYEE E ON RE.EMP_NO = E.EMP_NO
AND TO_CHAR(RE.RV_DATE,'YYYY-MM-DD') BETWEEN #{startDate} AND #{endDate}
order by RE.RV_DATE ) WHERE 1=1
AND RDATE BETWEEN #{startDate} AND #{endDate}
AND CT_NM LIKE '%'||#{searchTxt}||'%' OR RV_PH LIKE '%'||#{searchTxt}||'%' OR EMP_NM LIKE '%'||#{searchTxt}||'%' OR RV_CON LIKE '%'||#{searchTxt}||'%'
AND CT_NM LIKE '%'||#{searchTxt}||'%'
AND RV_PH LIKE '%'||#{searchTxt}||'%'
AND EMP_NM LIKE '%'||#{searchTxt}||'%'
AND RV_CON LIKE '%'||#{searchTxt}||'%'
AND RNUM BETWEEN #{startCnt} AND #{endCnt}

mapper.update:
UPDATE RESERVATION SET RV_DATE = TO_DATE(#{startDate}|| ' ' ||#{restime},'YYYY-MM-DD HH24:MI'),
CT_NO =#{txt_client_no}, RV_PH=#{txt_client_ph},EMP_NO =#{emp_no},RV_COLOR=#{edit-color},
RV_CON = #{txt_RV_CON}
WHERE RV_NO = #{res_no}
